import React, { useState } from 'react';
import ReactPlayer from 'react-player';

const Video = () => {
  // Создаём состояние для управления отображением видео
  const [isVideoVisible, setIsVideoVisible] = useState(false);

  // Функция для обработки нажатия на кнопку
  const toggleVideoVisibility = () => {
    setIsVideoVisible((prevValue) => !prevValue); // Переключаем состояние
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      {/* Кнопка для отображения/скрытия видео */}
      <button onClick={toggleVideoVisibility}>
        {isVideoVisible ? 'Скрыть видео' : 'Показать видео'}
      </button>

      {/* Видео отображается только если isVideoVisible === true */}
      {isVideoVisible && (
        <div style={{ marginTop: '20px' }}>
          <video
            width="600"
            controls // Добавляем элементы управления (пауза, воспроизведение, громкость)
          >
            <source src="./resources/vid.mp4" type="video/mp4" />
            Ваш браузер не поддерживает видеотег.
          </video>
        </div>
      )}
    </div>
  );
};

export default Video;
